ipt array The javaScript   *   * @return none   */  public static function addJavaScript($key, $javaScript) {  	    if (!is_array(self::$javaScript[$key])) {      self::$javaScript[$key] = array();    }    self::$javaScript[$key][] = $javaScript;     }  /**   * Gets the javaScript for a given key   *   * @param $key string The key   *   * @return string the javaScript   */  protected static function getJavaScript($key) {    return implode(chr(10) . '    ', self::$javaScript[$key]);  }    /**   * Returns the javaScript Header   *   * @param none   *   * @return string The javaScript Header   */  protected static function getJavaScriptHeader() {    $javaScript = array();    $javaScript[] = '';    $javaScript[] = '<script type="text/javascript">';    $javaScript[] = '/*<![CDATA[*/';    $javaScript[] = '  function checkIfRteChanged(x) {';    $javaScript[] = '    if (RTEarea[x].editor.plugins.UndoRedo.instance.undoPosition>0) {';    $javaScript[] = '      document.changed = true;';    $javaScript[] = '    }';    $javaScript[] = '  }';    $javaScript[] = '  function submitIfChanged() {';    $javaScript[] = '    ' . self::getJavaScript('checkIfRteChanged');    $javaScript[] = '    if (document.changed) {';    $javaScript[] = '      if (confirm("' . Tx_Extbase_Utility_Localization::translate('warning.save', Tx_SavLibraryPlus_Controller_AbstractController::LIBRARY_NAME) . '"))	{';    $javaScript[] = '        update();';    $javaScript[] = '        return false;';    $javaScript[] = '      }';    $javaScript[] = '      return true;';    $javaScript[] = '    }';    $javaScript[] = '    return true;';    $javaScript[] = '  }';    $javaScript[] = '  function update() {';    $javaScript[] = '    ' . self::getJavaScript('rteUpdate');    $javaScript[] = '    ' . self::getJavaScript('selectAll');    $javaScript[] = '        document.' . Tx_SavLibraryPlus_Controller_AbstractController::getFormName() . '.submit();';        $javaScript[] = '    return true;';    $javaScript[] = '  }';    $javaScript[] = '/*]]>*/';    $javaScript[] = '</script>';    return implode(chr(10), $javaScript);  }     }?>