riptConfiguration[$this->getController()->getFormConfigurationManager()->getFormTitle() . '.'];     if (is_array($formTypoScriptConfiguration) === false) {    	return NULL;    }        // Gets the TypoScript configuration        $typoScriptConfiguration = $formTypoScriptConfiguration[t3lib_div::lcfirst($type) . '.'];    if (is_array($typoScriptConfiguration) === false) {    	return NULL;    }      return $typoScriptConfiguration;	}	  /**   * Sets the view configuration files from the page TypoScript configuration   *   * @param none   *   * @return none   */  public function setViewConfigurationFilesFromPageTypoScriptConfiguration() {  	  	// Gets the view type  	$viewer = $this->getController()->getViewer();    if ($viewer === NULL) {			return;		}  	$viewType = $viewer->getViewType();  		// Gets the view page TypoScript configuration  		$viewTypoScriptConfiguration = $this->getTypoScriptConfiguration($viewType);		if ($viewTypoScriptConfiguration === NULL) {			return;		}		    // Processes the view configuration files    $viewConfigurationFiles = $viewTypoScriptConfiguration['configuration.'];    if (is_array($viewConfigurationFiles)) {    	foreach($viewConfigurationFiles as $viewConfigurationFileKey => $viewConfigurationFile) {    		    		switch($viewConfigurationFileKey) {    			case 'templateFile':    			case 'partialRootPath':    			case 'layoutRootPath':    				$setMethod = 'set' . ucfirst($viewConfigurationFileKey);				    				if (method_exists($viewer, $setMethod) === true) {    					$viewer->$setMethod($viewConfigurationFile);    				} else {							Tx_SavLibraryPlus_Controller_FlashMessages::addError('error.unknownSetMethodForPageTypoScriptConfiguration', array($setMethod, $viewType));							    					    				}    				break;    			default:						Tx_SavLibraryPlus_Controller_FlashMessages::addError('error.unknownPageTypoScriptConfiguration', array($viewConfigurationFileKey, $viewType));    				break;    		}    	}    }  }    /**   * Gets the view configuration field from the page TypoScript configuration   *   * @param string $fieldName   *   * @return array   */  public function getViewConfigurationFieldFromPageTypoScriptConfiguration($fieldName, $viewType = NULL) {	  // Gets the view type if none provided		if ($viewType === NULL) {	  	$viewer = $this->getController()->getViewer();	  	if ($viewer === NULL) {				return NULL;			}  		  	$viewType = $viewer->getViewType();		}		// Gets the view page TypoScript configuration  		$viewTypoScriptConfiguration = $this->getTypoScriptConfiguration($viewType);		if ($viewTypoScriptConfiguration === NULL) {			return NULL;		}		    // Processes the view configuration fields    $viewConfigurationFields = $viewTypoScriptConfiguration['fields.'];           // Processes the field name    $fieldNameParts = explode('.', $fieldName);    $tableNameWithDot = $fieldNameParts[0] . '.';    $fieldNameWithDot = $fieldNameParts[1] . '.'; 	    // Checks if the field is in the main table    $querier = $this->getController()->getQuerier();    if ($querier !== NULL) {    	$isMainTableField = $querier->getQueryConfigurationManager()->getMainTable() == $fieldNameParts[0];    } else {    	$isMainTableField = false;    }    	    // Builds the view field attributes configuration    if ($isMainTableField && is_array($viewConfigurationFields[$fieldNameWithDot])) {    	$viewConfigurationFieldAttributes = $viewConfigurationFields[$fieldNameWithDot];    } elseif (is_array($viewConfigurationFields[$tableNameWithDot][$fieldNameWithDot])) {    	$viewConfigurationFieldAttributes = $viewConfigurationFields[$tableNameWithDot][$fieldNameWithDot];    		    } else {    	return NULL;    }		// Processes the field attributes    $fieldAttributes = array();    foreach($viewConfigurationFieldAttributes as $viewConfigurationFieldAttributeKey => $viewConfigurationFieldAttribute) {    	$fieldAttributes[strtolower($viewConfigurationFieldAttributeKey)] = $viewConfigurationFieldAttribute;    }    return $fieldAttributes;  }   }?>