   * @return array   */  public static function getTcaConfigField($tableName, $fieldName) {    t3lib_div::loadTCA($tableName);    $config = $GLOBALS['TCA'][$tableName]['columns'][$fieldName]['config'];    return (is_array($config) ? $config : array());  }  /**   * Gets the TCA config for a full field name.   *   * @param   *   * @return array   */  public static function getTcaConfigFieldFromFullFieldName($fullFieldName) {  	$fieldNameParts = explode('.', $fullFieldName);  	return self::getTcaConfigField($fieldNameParts[0], $fieldNameParts[1]);  }    }?>